(2310, 219, '_mission_mission_content_0_content', 'field_61091fe12d422'),
(2311, 219, 'mission_mission_content_1_name', 'Sự kết nối và chia sẻ:'),
(2312, 219, '_mission_mission_content_1_name', 'field_61091fcc2d421'),
(2313, 219, 'mission_mission_content_1_content', 'Cùng bạn kiến tạo hành trình trải nghiệm cà phê thú vị dựa trên giá trị kết nối và chia sẻ.\r\n'),
(2314, 219, '_mission_mission_content_1_content', 'field_61091fe12d422'),
(2315, 219, 'mission_mission_content_2_name', 'Sự thịnh vượng của </br> Quý đối tác: '),
