(2323, 219, 'content__brand_culture_0_name', 'Minh bạch: '),
(2324, 219, '_content__brand_culture_0_name', 'field_61092087d4c1d'),
(2325, 219, 'content__brand_culture_0_content', 'Sự minh bạch sẽ thể hiện trong mọi chính sách của Công Ty. Tất cả thành viên đều phải minh bạch trong chính sách, trong tài chính, trong kế hoạch và trong mọi yêu cầu'),
(2326, 219, '_content__brand_culture_0_content', 'field_61092090d4c1e'),
(2327, 219, 'content__brand_culture_1_name', 'Công bằng: '),
(2328, 219, '_content__brand_culture_1_name', 'field_61092087d4c1d'),
(2329, 219, 'content__brand_culture_1_content', 'Để đảm bảo một môi trường vận hành tốt và luôn tạo ra sự thăng tiến cho tất cả các thành viên trong Công Ty đều được trao và tin tưởng về sự công bằng trong mọi vấn đề'),
(2330, 219, '_content__brand_culture_1_content', 'field_61092090d4c1e'),
(2331, 219, 'content__brand_culture_2_name', 'Trao quyền: '),
(2332, 219, '_content__brand_culture_2_name', 'field_61092087d4c1d'),
(2333, 219, 'content__brand_culture_2_content', 'Mọi thành viên trong Công Ty ở mọi vị trí đều được trao quyền thực thi, quyết định vấn đề thuộc chuyên môn, kiến thức và trách nhiệm của mình'),
(2334, 219, '_content__brand_culture_2_content', 'field_61092090d4c1e'),
(2335, 219, 'content__brand_culture_3_name', 'Sáng tạo:'),
(2336, 219, '_content__brand_culture_3_name', 'field_61092087d4c1d'),
(2337, 219, 'content__brand_culture_3_content', 'Sáng tạo để thay đổi mọi thứ, mọi thành viên của Công Ty luôn luôn được khuyến khích và sẽ được đánh giá cao nếu tạo ra mọi sự sáng tạo trong công việc và kế hoạch, tất cả mọi vấn đề đều được thảo luận và cùng đóng góp ý tưởng'),
(2338, 219, '_content__brand_culture_3_content', 'field_61092090d4c1e'),
(2339, 219, 'content__brand_culture_4_name', 'Chất lượng: '),
(2340, 219, '_content__brand_culture_4_name', 'field_61092087d4c1d'),
(2341, 219, 'content__brand_culture_4_content', 'Chất lượng sản phẩm, dịch vụ, công việc được tạo ra là lựa chọn đầu tiên của Công Ty, không đánh đổi các giá trị khác với giá trị chất lượng'),
